SET TERM ^ ;ALTER PROCEDURE MP_USERCLAIMS_INSERT (    USERID varchar(128),    CLAIMTYPE blob sub_type 1,    CLAIMVALUE blob sub_type 1,	SiteId integer)RETURNS (    ID integer )ASBEGIN ID = NEXT VALUE FOR mp_UserClaims_seq;INSERT INTO 	mp_USERCLAIMS(								SiteId,                ID,                USERID,                CLAIMTYPE,                CLAIMVALUE) VALUES (							   :SiteId,               :ID,               :USERID,               :CLAIMTYPE,               :CLAIMVALUE);END^SET TERM ; ^